mapper.insert:

insert into ORD_ADDR (ID, USER_ID, RECEIVER_NAME, 
  RECEIVER_TEL, RECEIVER_MOBILE, RECEIVER_PROVINCE, 
  RECEIVER_CITY, RECEIVER_EXP_AREA, RECEIVER_ADDRESS, 
  RECEIVER_POST_CODE, IS_DEF, OP_TIME
  )
values (#{id,jdbcType=BIGINT}, #{userId,jdbcType=BIGINT}, #{receiverName,jdbcType=VARCHAR}, 
  #{receiverTel,jdbcType=VARCHAR}, #{receiverMobile,jdbcType=VARCHAR}, #{receiverProvince,jdbcType=VARCHAR}, 
  #{receiverCity,jdbcType=VARCHAR}, #{receiverExpArea,jdbcType=VARCHAR}, #{receiverAddress,jdbcType=VARCHAR}, 
  #{receiverPostCode,jdbcType=CHAR}, #{isDef,jdbcType=TINYINT}, #{opTime,jdbcType=TIMESTAMP}
  )

mapper.select:

select 

from ORD_ADDR


    and USER_ID = #{userId,jdbcType=BIGINT}


    and RECEIVER_NAME = #{receiverName,jdbcType=VARCHAR}


    and RECEIVER_TEL = #{receiverTel,jdbcType=VARCHAR}


    and RECEIVER_MOBILE = #{receiverMobile,jdbcType=VARCHAR}


    and RECEIVER_PROVINCE = #{receiverProvince,jdbcType=VARCHAR}


    and RECEIVER_CITY = #{receiverCity,jdbcType=VARCHAR}


    and RECEIVER_EXP_AREA = #{receiverExpArea,jdbcType=VARCHAR}


    and RECEIVER_ADDRESS = #{receiverAddress,jdbcType=VARCHAR}


    and RECEIVER_POST_CODE = #{receiverPostCode,jdbcType=CHAR}


    and IS_DEF = #{isDef,jdbcType=TINYINT}


    and OP_TIME = #{opTime,jdbcType=TIMESTAMP}


select count(*) 
from ORD_ADDR
where ID = #{id,jdbcType=BIGINT}


