mapper.update:
UPDATE ORD_ORDER_DETAIL
SET
	RETURN_COUNT=#{returnTotal,jdbcType=INTEGER},
	CASHBACK_TOTAL=#{newCashbackTotal,jdbcType=DECIMAL}

		,RETURN_STATE = #{returnState,jdbcType=TINYINT}

WHERE 
	ID=#{id,jdbcType=BIGINT}
	AND RETURN_COUNT=#{returnedCount,jdbcType=INTEGER}
	AND CASHBACK_TOTAL=#{oldCashbackTotal,jdbcType=DECIMAL}

UPDATE ORD_ORDER_DETAIL SET COMMISSION_SLOT = #{cashbackCommissionSlot,jdbcType=TINYINT} WHERE ID = #{id,jdbcType=BIGINT}
AND
COMMISSION_SLOT = #{cashbackCommissionSlot,jdbcType=TINYINT}

UPDATE ORD_ORDER_DETAIL SET COMMISSION_SLOT = #{commissionSlot,jdbcType=TINYINT} WHERE ID =
#{id,jdbcType=BIGINT}
AND COMMISSION_SLOT = ${commissionSlot}+1

mapper.select:
SELECT ood.ID, ood.ORDER_ID, ood.ONLINE_ID, ood.PRODUCT_ID, ood.ONLINE_TITLE, ood.SPEC_NAME, ood.BUY_COUNT, ood.BUY_PRICE,
ood.CASHBACK_AMOUNT, ood.RETURN_COUNT,
ood.CASHBACK_TOTAL, ood.BUY_UNIT, RETURN_STATE, ood.USER_ID, ood.COMMISSION_SLOT,
ood.COMMISSION_STATE
from ORD_ORDER_DETAIL ood , ORD_ORDER oo,ORD_BUY_RELATION oor


		and ood.ORDER_ID = #{orderId,jdbcType=BIGINT}


		and ood.ONLINE_ID = #{onlineId,jdbcType=BIGINT}


		and ood.PRODUCT_ID = #{productId,jdbcType=BIGINT}


		and ood.SUBJECT_TYPE = #{subjectType,jdbcType=TINYINT}


		and ood.COMMISSION_SLOT = #{commissionSlot,jdbcType=TINYINT}


		and ood.COMMISSION_STATE = #{commissionState,jdbcType=TINYINT}


		and ood.ONLINE_TITLE = #{onlineTitle,jdbcType=VARCHAR}


		and ood.SPEC_NAME = #{specName,jdbcType=VARCHAR}


		and ood.BUY_COUNT = #{buyCount,jdbcType=INTEGER}


		and ood.BUY_PRICE = #{buyPrice,jdbcType=DECIMAL}


		and ood.CASHBACK_AMOUNT = #{cashbackAmount,jdbcType=DECIMAL}


		and ood.RETURN_COUNT = #{returnCount,jdbcType=INTEGER}


		and ood.CASHBACK_TOTAL = #{cashbackTotal,jdbcType=DECIMAL}


		and ood.BUY_UNIT = #{buyUnit,jdbcType=VARCHAR}


		and ood.RETURN_STATE = #{returnState,jdbcType=TINYINT}


		and ood.USER_ID = #{userId,jdbcType=BIGINT}


		and ood.ID != #{downLineRelationId1,jdbcType=BIGINT}


		and ood.ID != #{downLineRelationId2,jdbcType=BIGINT}


		and ood.orderTimestamp <= #{orderTimestamp,jdbcType=BIGINT}


AND ood.ORDER_ID = oo.ID and ood.ID != #{id,jdbcType=BIGINT} and ood.ID = oor.UPLINE_ORDER_DETAIL_ID and oor.RELATION_SOURCE != 1
and oo.ORDER_STATE IN (2,3,4,5) and
ood.COMMISSION_SLOT >0 ORDER BY oo.ORDER_TIME ASC limit 1

SELECT ood.ID, ood.ORDER_ID, ood.ONLINE_ID, ood.PRODUCT_ID, ood.ONLINE_TITLE, ood.SPEC_NAME, ood.BUY_COUNT, ood.BUY_PRICE,
ood.CASHBACK_AMOUNT, ood.RETURN_COUNT,
ood.CASHBACK_TOTAL, ood.BUY_UNIT, RETURN_STATE, ood.USER_ID, ood.COMMISSION_SLOT,
ood.COMMISSION_STATE
from ORD_ORDER_DETAIL ood , ORD_ORDER oo


		and ood.ORDER_ID = #{orderId,jdbcType=BIGINT}


		and ood.ONLINE_ID = #{onlineId,jdbcType=BIGINT}


		and ood.PRODUCT_ID = #{productId,jdbcType=BIGINT}


		and ood.SUBJECT_TYPE = #{subjectType,jdbcType=TINYINT}


		and ood.COMMISSION_SLOT = #{commissionSlot,jdbcType=TINYINT}


		and ood.COMMISSION_STATE = #{commissionState,jdbcType=TINYINT}


		and ood.ONLINE_TITLE = #{onlineTitle,jdbcType=VARCHAR}


		and ood.SPEC_NAME = #{specName,jdbcType=VARCHAR}


		and ood.BUY_COUNT = #{buyCount,jdbcType=INTEGER}


		and ood.BUY_PRICE = #{buyPrice,jdbcType=DECIMAL}


		and ood.CASHBACK_AMOUNT = #{cashbackAmount,jdbcType=DECIMAL}


		and ood.RETURN_COUNT = #{returnCount,jdbcType=INTEGER}


		and ood.CASHBACK_TOTAL = #{cashbackTotal,jdbcType=DECIMAL}


		and ood.BUY_UNIT = #{buyUnit,jdbcType=VARCHAR}


		and ood.RETURN_STATE = #{returnState,jdbcType=TINYINT}


		and ood.USER_ID = #{userId,jdbcType=BIGINT}


		and ood.ID != #{downLineRelationId1,jdbcType=BIGINT}


		and ood.ID != #{downLineRelationId2,jdbcType=BIGINT}


		and ood.orderTimestamp <= #{orderTimestamp,jdbcType=BIGINT}


AND ood.ORDER_ID = oo.ID and ood.ID != #{id,jdbcType=BIGINT} and oo.ORDER_STATE IN (2,3,4,5) and ood.COMMISSION_SLOT >0 ORDER BY oo.ORDER_TIME ASC limit 1

SELECT ood.ID, ood.ORDER_ID, ood.ONLINE_ID, ood.PRODUCT_ID, ood.ONLINE_TITLE, ood.SPEC_NAME, ood.BUY_COUNT, ood.BUY_PRICE,
ood.CASHBACK_AMOUNT, ood.RETURN_COUNT,
ood.CASHBACK_TOTAL, ood.BUY_UNIT, RETURN_STATE, ood.USER_ID, ood.COMMISSION_SLOT,
ood.COMMISSION_STATE
from ORD_ORDER_DETAIL ood , ORD_ORDER oo,ORD_BUY_RELATION oor


		and ood.ORDER_ID = #{orderId,jdbcType=BIGINT}


		and ood.ONLINE_ID = #{onlineId,jdbcType=BIGINT}


		and ood.PRODUCT_ID = #{productId,jdbcType=BIGINT}


		and ood.SUBJECT_TYPE = #{subjectType,jdbcType=TINYINT}


		and ood.COMMISSION_SLOT = #{commissionSlot,jdbcType=TINYINT}


		and ood.COMMISSION_STATE = #{commissionState,jdbcType=TINYINT}


		and ood.ONLINE_TITLE = #{onlineTitle,jdbcType=VARCHAR}


		and ood.SPEC_NAME = #{specName,jdbcType=VARCHAR}


		and ood.BUY_COUNT = #{buyCount,jdbcType=INTEGER}


		and ood.BUY_PRICE = #{buyPrice,jdbcType=DECIMAL}


		and ood.CASHBACK_AMOUNT = #{cashbackAmount,jdbcType=DECIMAL}


		and ood.RETURN_COUNT = #{returnCount,jdbcType=INTEGER}


		and ood.CASHBACK_TOTAL = #{cashbackTotal,jdbcType=DECIMAL}


		and ood.BUY_UNIT = #{buyUnit,jdbcType=VARCHAR}


		and ood.RETURN_STATE = #{returnState,jdbcType=TINYINT}


		and ood.USER_ID = #{userId,jdbcType=BIGINT}


		and ood.ID != #{downLineRelationId1,jdbcType=BIGINT}


		and ood.ID != #{downLineRelationId2,jdbcType=BIGINT}


AND ood.ORDER_ID = oo.ID and ood.id = oor.UPLINE_ORDER_DETAIL_ID and oor.RELATION_SOURCE IN (1,2) and oo.ORDER_STATE IN (2,3,4,5) and ood.COMMISSION_SLOT > 0 ORDER BY oo.ORDER_TIME
ASC limit 1

SELECT ood.ID, ood.ORDER_ID, ood.ONLINE_ID, ood.PRODUCT_ID, ood.ONLINE_TITLE, ood.SPEC_NAME, ood.BUY_COUNT, ood.BUY_PRICE,
ood.CASHBACK_AMOUNT, ood.RETURN_COUNT,
ood.CASHBACK_TOTAL, ood.BUY_UNIT, RETURN_STATE, ood.USER_ID, ood.COMMISSION_SLOT,
ood.COMMISSION_STATE
from ORD_ORDER_DETAIL ood , ORD_ORDER oo,ORD_GOODS_BUY_RELATION oog


		and ood.ORDER_ID = #{orderId,jdbcType=BIGINT}


		and ood.ONLINE_ID = #{onlineId,jdbcType=BIGINT}


		and ood.PRODUCT_ID = #{productId,jdbcType=BIGINT}


		and ood.SUBJECT_TYPE = #{subjectType,jdbcType=TINYINT}


		and ood.COMMISSION_SLOT = #{commissionSlot,jdbcType=TINYINT}


		and ood.COMMISSION_STATE = #{commissionState,jdbcType=TINYINT}


		and ood.ONLINE_TITLE = #{onlineTitle,jdbcType=VARCHAR}


		and ood.SPEC_NAME = #{specName,jdbcType=VARCHAR}


		and ood.BUY_COUNT = #{buyCount,jdbcType=INTEGER}


		and ood.BUY_PRICE = #{buyPrice,jdbcType=DECIMAL}


		and ood.CASHBACK_AMOUNT = #{cashbackAmount,jdbcType=DECIMAL}


		and ood.RETURN_COUNT = #{returnCount,jdbcType=INTEGER}


		and ood.CASHBACK_TOTAL = #{cashbackTotal,jdbcType=DECIMAL}


		and ood.BUY_UNIT = #{buyUnit,jdbcType=VARCHAR}


		and ood.RETURN_STATE = #{returnState,jdbcType=TINYINT}


		and ood.USER_ID = #{userId,jdbcType=BIGINT}


		and ood.ID != #{downLineRelationId1,jdbcType=BIGINT}


		and ood.ID != #{downLineRelationId2,jdbcType=BIGINT}


AND ood.ORDER_ID = oo.ID and oog.DOWNLINE_USER_ID = #{downLineUserId,jdbcType=TINYINT}
and ood.USER_ID = oog.UPLINE_USER_ID and oo.ORDER_STATE IN (2,3,4,5) and ood.COMMISSION_SLOT
> 0 ORDER BY oog.CREATE_TIME ASC limit 1
