mapper.select:

select count(*) 
from ORD_ORDER_DETAIL
where ID = #{id,jdbcType=BIGINT}


select count(*) 
from ORD_ORDER_DETAIL


    and ID = #{id,jdbcType=BIGINT}


    and ORDER_ID = #{orderId,jdbcType=BIGINT}


    and ONLINE_ID = #{onlineId,jdbcType=BIGINT}


    and PRODUCE_ID = #{produceId,jdbcType=BIGINT}


    and ONLINE_TITLE = #{onlineTitle,jdbcType=VARCHAR}


    and SPEC_NAME = #{specName,jdbcType=VARCHAR}


    and BUY_COUNT = #{buyCount,jdbcType=INTEGER}


    and BUY_PRICE = #{buyPrice,jdbcType=DECIMAL}


    and CASHBACK_AMOUNT = #{cashbackAmount,jdbcType=DECIMAL}


    and RETURN_COUNT = #{returnCount,jdbcType=INTEGER}


    and CASHBACK_TOTAL = #{cashbackTotal,jdbcType=DECIMAL}


    and BUY_UNIT = #{buyUnit,jdbcType=VARCHAR}


    and RETURN_STATE = #{returnState,jdbcType=TINYINT}


    and USER_ID = #{userId,jdbcType=BIGINT}


    and CASHBACK_COMMISSION_SLOT = #{cashbackCommissionSlot,jdbcType=TINYINT}


    and CASHBACK_COMMISSION_STATE = #{cashbackCommissionState,jdbcType=TINYINT}


select 

from ORD_ORDER_DETAIL


    and ID = #{id,jdbcType=BIGINT}


    and ORDER_ID = #{orderId,jdbcType=BIGINT}


    and ONLINE_ID = #{onlineId,jdbcType=BIGINT}


    and PRODUCE_ID = #{produceId,jdbcType=BIGINT}


    and ONLINE_TITLE = #{onlineTitle,jdbcType=VARCHAR}


    and SPEC_NAME = #{specName,jdbcType=VARCHAR}


    and BUY_COUNT = #{buyCount,jdbcType=INTEGER}


    and BUY_PRICE = #{buyPrice,jdbcType=DECIMAL}


    and CASHBACK_AMOUNT = #{cashbackAmount,jdbcType=DECIMAL}


    and RETURN_COUNT = #{returnCount,jdbcType=INTEGER}


    and CASHBACK_TOTAL = #{cashbackTotal,jdbcType=DECIMAL}


    and BUY_UNIT = #{buyUnit,jdbcType=VARCHAR}


    and RETURN_STATE = #{returnState,jdbcType=TINYINT}


    and USER_ID = #{userId,jdbcType=BIGINT}

mapper.update:
	UPDATE ORD_ORDER_DETAIL SET RETURN_COUNT=#{returnCount,jdbcType=INTEGER}, CASHBACK_TOTAL=#{cashbackTotal,jdbcType=DECIMAL} 

    ,RETURN_STATE = #{returnState,jdbcType=TINYINT}

	WHERE ID=#{id,jdbcType=BIGINT} AND ORDER_ID=#{orderId,jdbcType=BIGINT}

mapper.select:
	 SELECT  ood.ID, ood.ORDER_ID, ood.ONLINE_ID, ood.PRODUCE_ID, ood.ONLINE_TITLE, ood.SPEC_NAME, ood.BUY_COUNT, ood.BUY_PRICE, 
  ood.CASHBACK_AMOUNT, ood.RETURN_COUNT, ood.CASHBACK_TOTAL, ood.BUY_UNIT, RETURN_STATE, ood.USER_ID, ood.CASHBACK_COMMISSION_SLOT, 
  ood.CASHBACK_COMMISSION_STATE,oo.ORDER_TIME
  from ORD_ORDER_DETAIL ood AND ORD_ORDER oo


      and ood.ID = #{id,jdbcType=BIGINT}


      and ood.ORDER_ID = #{orderId,jdbcType=BIGINT}


      and ood.ONLINE_ID = #{onlineId,jdbcType=BIGINT}


      and ood.PRODUCE_ID = #{produceId,jdbcType=BIGINT}


      and ood.ONLINE_TITLE = #{onlineTitle,jdbcType=VARCHAR}


      and ood.SPEC_NAME = #{specName,jdbcType=VARCHAR}


      and ood.BUY_COUNT = #{buyCount,jdbcType=INTEGER}


      and ood.BUY_PRICE = #{buyPrice,jdbcType=DECIMAL}


      and ood.CASHBACK_AMOUNT = #{cashbackAmount,jdbcType=DECIMAL}


      and ood.RETURN_COUNT = #{returnCount,jdbcType=INTEGER}


      and ood.CASHBACK_TOTAL = #{cashbackTotal,jdbcType=DECIMAL}


      and ood.BUY_UNIT = #{buyUnit,jdbcType=VARCHAR}


      and ood.RETURN_STATE = #{returnState,jdbcType=TINYINT}


      and ood.USER_ID = #{userId,jdbcType=BIGINT}


  AND ood.ORDER_ID = oo.ID and ood.CASHBACK_COMMISSION_SLOT < 2  ORDER BY oo.ORDER_TIME ASC limit 1
