mapper.select:

select


from ORD_ORDER


		and USER_NAME = #{userName,jdbcType=VARCHAR}


		and ORDER_CODE = #{orderCode,jdbcType=VARCHAR}


		and ORDER_TIME >= #{orderTimeStart,jdbcType=TIMESTAMP}


		and ORDER_TIME <= #{orderTimeEnd,jdbcType=TIMESTAMP}


		and ORDER_STATE = #{orderState,jdbcType=TINYINT}



ORDER BY ORDER_TIME DESC
