select count(1) 
from ORD_RETURN


    and RETURN_CODE = #{returnCode,jdbcType=BIGINT}


    and ORDER_ID = #{orderId,jdbcType=BIGINT}


    and ORDER_DETAIL_ID = #{orderDetailId,jdbcType=BIGINT}


    and RETURN_COUNT = #{returnCount,jdbcType=INTEGER}


    and RETURN_RENTAL = #{returnRental,jdbcType=DECIMAL}


    and RETURN_AMOUNT1 = #{returnAmount1,jdbcType=DECIMAL}


    and RETURN_AMOUNT2 = #{returnAmount2,jdbcType=DECIMAL}


    and SUBTRACT_CASHBACK = #{subtractCashback,jdbcType=DECIMAL}


    and RETURN_TYPE = #{returnType,jdbcType=TINYINT}


    and APPLICATION_STATE = #{applicationState,jdbcType=TINYINT}


    and REFUND_STATE = #{refundState,jdbcType=TINYINT}


    and RETURN_REASON = #{returnReason,jdbcType=VARCHAR}


    and USER_ID = #{userId,jdbcType=BIGINT}


    and APPLICATION_OP_ID = #{applicationOpId,jdbcType=BIGINT}


    and APPLICATION_TIME = #{applicationTime,jdbcType=TIMESTAMP}


    and CANCEL_OP_ID = #{cancelOpId,jdbcType=BIGINT}


    and CANCEL_TIME = #{cancelTime,jdbcType=TIMESTAMP}


    and REVIEW_OP_ID = #{reviewOpId,jdbcType=BIGINT}


    and REVIEW_TIME = #{reviewTime,jdbcType=TIMESTAMP}


    and REFUND_OP_ID = #{refundOpId,jdbcType=BIGINT}


    and REFUND_TIME = #{refundTime,jdbcType=TIMESTAMP}


    and REJECT_OP_ID = #{rejectOpId,jdbcType=BIGINT}


    and REJECT_REASON = #{rejectReason,jdbcType=VARCHAR}


    and REJECT_TIME = #{rejectTime,jdbcType=TIMESTAMP}


    and FINISH_OP_ID = #{finishOpId,jdbcType=BIGINT}


    and FINISH_TIME = #{finishTime,jdbcType=TIMESTAMP}


    and RECEIVE_OP_ID = #{receiveOpId,jdbcType=BIGINT}


    and RECEIVE_TIME = #{receiveTime,jdbcType=TIMESTAMP}

  	SELECT a.*, c.USER_NAME, b.ONLINE_ID, b.PRODUCT_ID, b.ONLINE_TITLE, b.SPEC_NAME, b.BUY_COUNT, b.BUY_PRICE, b.CASHBACK_AMOUNT, b.RETURN_STATE, b.BUY_UNIT
	FROM ORD_RETURN a
	LEFT JOIN ORD_ORDER_DETAIL b ON a.ORDER_DETAIL_ID=b.ID AND a.ORDER_ID=b.ORDER_ID,ORD_ORDER c
	WHERE 1=1 and c.ID=a.ORDER_ID

      and a.RETURN_CODE = #{returnCode,jdbcType=BIGINT}


      and a.ORDER_ID = #{orderId,jdbcType=BIGINT}


      and APPLICATION_STATE = #{applicationState,jdbcType=TINYINT}

    ORDER BY APPLICATION_TIME DESC

mapper.update:
UPDATE ORD_RETURN SET APPLICATION_STATE=4, REFUND_STATE=1, REJECT_OP_ID = #{rejectOpId,jdbcType=BIGINT}, REJECT_REASON = #{rejectReason,jdbcType=VARCHAR}, 
REJECT_TIME = #{rejectTime,jdbcType=TIMESTAMP}, FINISH_OP_ID = #{finishOpId,jdbcType=BIGINT}, FINISH_TIME = #{finishTime,jdbcType=TIMESTAMP}
WHERE RETURN_CODE = #{returnCode,jdbcType=BIGINT}

  	UPDATE ORD_RETURN SET RETURN_RENTAL = #{returnRental,jdbcType=DECIMAL}, RETURN_AMOUNT1 = #{returnAmount1,jdbcType=DECIMAL}, RETURN_AMOUNT2 = #{returnAmount2,jdbcType=DECIMAL},
	SUBTRACT_CASHBACK = #{subtractCashback,jdbcType=DECIMAL}, APPLICATION_STATE=3, REFUND_STATE=2, REFUND_OP_ID = #{refundOpId,jdbcType=BIGINT}, REFUND_TIME = #{refundTime,jdbcType=TIMESTAMP},
	FINISH_OP_ID = #{finishOpId,jdbcType=BIGINT}, FINISH_TIME = #{finishTime,jdbcType=TIMESTAMP} WHERE RETURN_CODE = #{returnCode,jdbcType=BIGINT}

UPDATE ORD_RETURN SET APPLICATION_STATE=3, REFUND_STATE = #{refundState,jdbcType=TINYINT}, REFUND_OP_ID=#{refundOpId,jdbcType=BIGINT}, REFUND_TIME=#{refundTime,jdbcType=TIMESTAMP}, 
RECEIVE_OP_ID = #{receiveOpId,jdbcType=BIGINT}, RECEIVE_TIME = #{receiveTime,jdbcType=TIMESTAMP}, FINISH_OP_ID = #{finishOpId,jdbcType=BIGINT}, FINISH_TIME = #{finishTime,jdbcType=TIMESTAMP}
 WHERE RETURN_CODE = #{returnCode,jdbcType=BIGINT}

