select count(1) 
from ORD_RETURN


    and RETURN_CODE = #{returnCode,jdbcType=BIGINT}


    and ORDER_ID = #{orderId,jdbcType=BIGINT}


    and ORDER_DETAIL_ID = #{orderDetailId,jdbcType=BIGINT}


    and RETURN_COUNT = #{returnCount,jdbcType=INTEGER}


    and REFUND_TOTAL = #{refundTotal,jdbcType=DECIMAL}


    and REFUND_COMPENSATION = #{refundCompensation,jdbcType=DECIMAL}


    and RETURN_TYPE = #{returnType,jdbcType=TINYINT}


    and APPLICATION_STATE = #{applicationState,jdbcType=TINYINT}


    and RETURN_REASON = #{returnReason,jdbcType=VARCHAR}


    and USER_ID = #{userId,jdbcType=BIGINT}


    and APPLICATION_OP_ID = #{applicationOpId,jdbcType=BIGINT}


    and APPLICATION_TIME = #{applicationTime,jdbcType=TIMESTAMP}


    and CANCEL_OP_ID = #{cancelOpId,jdbcType=BIGINT}


    and CANCEL_TIME = #{cancelTime,jdbcType=TIMESTAMP}


    and REVIEW_OP_ID = #{reviewOpId,jdbcType=BIGINT}


    and REVIEW_TIME = #{reviewTime,jdbcType=TIMESTAMP}


    and REFUND_OP_ID = #{refundOpId,jdbcType=BIGINT}


    and REFUND_TIME = #{refundTime,jdbcType=TIMESTAMP}


    and REJECT_OP_ID = #{rejectOpId,jdbcType=BIGINT}


    and REJECT_REASON = #{rejectReason,jdbcType=VARCHAR}


    and REJECT_TIME = #{rejectTime,jdbcType=TIMESTAMP}


    and FINISH_OP_ID = #{finishOpId,jdbcType=BIGINT}


    and FINISH_TIME = #{finishTime,jdbcType=TIMESTAMP}


    and RECEIVE_OP_ID = #{receiveOpId,jdbcType=BIGINT}


    and RECEIVE_TIME = #{receiveTime,jdbcType=TIMESTAMP}

SELECT a.*, c.RECEIVER_NAME, b.ONLINE_ID, b.PRODUCT_ID, b.ONLINE_TITLE, b.SPEC_NAME, 
if(a.RETURN_COUNT * b.BUY_PRICE = 0, b.ACTUAL_AMOUNT, a.RETURN_COUNT * b.BUY_PRICE) as RETURN_RENTAL,
b.BUY_COUNT, b.BUY_PRICE, b.CASHBACK_AMOUNT, b.RETURN_STATE, b.BUY_UNIT,c.ORDER_CODE,
c.RECEIVED_TIME, c.ORDER_STATE, b.SUBJECT_TYPE, b.COMMISSION_SLOT, b.COMMISSION_STATE 
FROM ORD_RETURN a
LEFT JOIN ORD_ORDER_DETAIL b ON a.ORDER_DETAIL_ID=b.ID AND a.ORDER_ID=b.ORDER_ID
left join ORD_ORDER c on a.ORDER_ID=c.ID
WHERE 1=1 and c.ID=a.ORDER_ID

	and a.RETURN_CODE = #{returnCode,jdbcType=BIGINT}


	and b.RETURN_STATE = #{returnState,jdbcType=BIGINT}


	and a.RETURN_TYPE = #{returnType,jdbcType=BIGINT}


	and c.USER_NAME = #{userName,jdbcType=VARCHAR}


	and a.ORDER_ID = #{orderId,jdbcType=BIGINT}


	and c.ORDER_CODE = #{orderCode,jdbcType=VARCHAR}


	and APPLICATION_STATE = #{applicationState,jdbcType=TINYINT}

ORDER BY APPLICATION_TIME DESC

mapper.update:
UPDATE ORD_RETURN SET APPLICATION_STATE=4, REJECT_OP_ID = #{rejectOpId,jdbcType=BIGINT}, REJECT_REASON = #{rejectReason,jdbcType=VARCHAR},
REJECT_TIME = #{rejectTime,jdbcType=TIMESTAMP}, FINISH_OP_ID = #{finishOpId,jdbcType=BIGINT}, FINISH_TIME = #{finishTime,jdbcType=TIMESTAMP}
WHERE ID = #{id,jdbcType=BIGINT}

update ORD_RETURN set REFUND_TOTAL= #{refundTotal,jdbcType=DECIMAL},
REFUND_COMPENSATION = #{refundCompensation,jdbcType=DECIMAL}, 	APPLICATION_STATE=3,
REFUND_OP_ID = #{refundOpId,jdbcType=BIGINT}, 					REFUND_TIME = #{refundTime,jdbcType=TIMESTAMP},
FINISH_OP_ID = #{finishOpId,jdbcType=BIGINT},					FINISH_TIME = #{finishTime,jdbcType=TIMESTAMP}, 
RECEIVE_OP_ID = #{receiveOpId,jdbcType=BIGINT},					RECEIVE_TIME = #{receiveTime,jdbcType=TIMESTAMP} 
where ID = #{id,jdbcType=BIGINT} and APPLICATION_STATE = #{applicationState,jdbcType=TINYINT}

