    and TASK_TYPE = #{taskType,jdbcType=TINYINT}


    and ORDER_ID = #{orderId,jdbcType=VARCHAR}


    and SUB_TASK_TYPE = #{subTaskType,jdbcType=TINYINT}


select count(1) 
from ORD_TASK


    and EXECUTE_STATE = #{executeState,jdbcType=TINYINT}


